        "keywords": "source:paper, user:professional, granularity:multi-object_preview, granularity:single-object_preview, granularity:discrete_overview, non-temporal_encoding:maps, non-temporal_encoding:networks, non-temporal_encoding:other, temporal_encoding:other, task:elementary, task:synoptic, task:temporal, object_type:image, object_type:text, data:time, data:place, data:text, data:ontology, interactivity:search, interactivity:analysis_and_orientation, interactivity:detail",
        "pages": "539--546",
