        "doi": "10.1109/IV.2014.47",
        "keywords": "source:paper, user:professional, granularity:multi-object_preview, granularity:single-object_preview, granularity:discrete_overview, granularity:abstract_overview, non-temporal_encoding:list_and_slideshow, non-temporal_encoding:grid_and_mosaic, non-temporal_encoding:networks, temporal_encoding:time_axis(2D), task:elementary, task:temporal, object_type:image, data:object, data:time, data:actor, data:relation, interactivity:search, interactivity:analysis_and_orientation, interactivity:vertical_immersion, interactivity:detail, interactivity:horizontal_browsing, evaluation:yes",
