        "online": "http://www.alicethudt.de/BohemianBookshelf/Program/BB.swf",
        "pages": "1461--1470",
        "publisher": "ACM",
        "series": "{CHI}",
        "shorttitle": "The {Bohemian} {Bookshelf}",
